// MusicPlayer.jsx
import React, { useState, useEffect } from 'react';
import { Play, Pause, SkipBack, SkipForward, Volume2 } from 'lucide-react';

// Queue List Component
const QueueList = () => {
  const [queue, setQueue] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchQueue = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/queue');
        const data = await response.json();

        if (data.error) {
          setError(data.error);
        } else {
          setQueue(data.queue || []);
        }
      } catch (error) {
        setError('Failed to fetch queue');
        console.error('Error:', error);
      }
    };

    // Fetch initially and then every 5 seconds
    fetchQueue();
    const interval = setInterval(fetchQueue, 5000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="bg-zinc-900/50 rounded-lg p-4">
      <h2 className="text-xl font-bold mb-4 text-white">Queue</h2>
      {error && (
        <div className="text-red-500 mb-4">{error}</div>
      )}
      <div className="space-y-2">
        {queue.map((track, index) => (
          <div 
            key={`${track.name}-${index}`}
            className="flex items-center space-x-3 p-2 rounded-md hover:bg-zinc-800/50 transition-colors"
          >
            {track.album_art && (
              <img 
                src={track.album_art} 
                alt={track.name}
                className="w-10 h-10 rounded"
              />
            )}
            <div className="flex-1 min-w-0">
              <div className="text-sm font-medium text-white truncate">
                {track.name}
              </div>
              <div className="text-xs text-zinc-400 truncate">
                {track.artist}
              </div>
            </div>
            <div className="text-xs text-zinc-500">
              {Math.floor(track.duration_ms / 60000)}:
              {String(Math.floor((track.duration_ms % 60000) / 1000)).padStart(2, '0')}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Main MusicPlayer Component
const MusicPlayer = () => {
  const [playbackState, setPlaybackState] = useState({
    track: '',
    artist: '',
    album_url: '',
    is_playing: false,
    progress_ms: 0,
    duration_ms: 0
  });
  const [handStatus, setHandStatus] = useState('inactive');

  useEffect(() => {
    const fetchPlayback = async () => {
      try {
        console.log('Fetching playback data...');
        const response = await fetch('http://localhost:5001/api/playback');
        const data = await response.json();
        console.log('Playback data:', data);
        if (!data.error) {
          setPlaybackState(data);
        }
      } catch (error) {
        console.error('Error fetching playback:', error);
      }
    };

    const fetchHandStatus = async () => {
      try {
        console.log('Fetching hand status...');
        const response = await fetch('http://localhost:5001/api/status');
        const data = await response.json();
        console.log('Hand status:', data);
        if (!data.error) {
          setHandStatus(data.hand_status);
        }
      } catch (error) {
        console.error('Error fetching hand status:', error);
      }
    };

    // Initial fetches
    fetchPlayback();
    fetchHandStatus();

    // Set up intervals
    const playbackInterval = setInterval(fetchPlayback, 1000);
    const statusInterval = setInterval(fetchHandStatus, 100);

    return () => {
      clearInterval(playbackInterval);
      clearInterval(statusInterval);
    };
  }, []);

  const formatTime = (ms) => {
    const seconds = Math.floor(ms / 1000);
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return 'bg-green-500';
      case 'warning': return 'bg-yellow-500';
      default: return 'bg-red-500';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-zinc-900 to-black text-white p-8">
      <div className="max-w-7xl mx-auto grid grid-cols-3 gap-8">
        {/* Main Player Section */}
        <div className="col-span-2 space-y-8">
          <div className="space-y-6">
            {/* Album Art */}
            <div className="relative group">
              <img 
                src={playbackState.album_url || '/api/placeholder/400/400'} 
                alt="Album Cover" 
                className="w-full aspect-square rounded-lg shadow-2xl group-hover:shadow-indigo-500/25 transition-all duration-300"
              />
            </div>

            {/* Track Info */}
            <div className="space-y-2">
              <h1 className="text-3xl font-bold">{playbackState.track}</h1>
              <p className="text-zinc-400 text-lg">{playbackState.artist}</p>
            </div>

            {/* Progress Bar */}
            <div className="space-y-2">
              <div className="h-1 bg-zinc-800 rounded-full">
                <div 
                  className="h-full bg-white rounded-full hover:bg-green-500 transition-colors duration-200"
                  style={{ 
                    width: `${(playbackState.progress_ms / playbackState.duration_ms) * 100}%` 
                  }}
                />
              </div>
              <div className="flex justify-between text-sm text-zinc-400">
                <span>{formatTime(playbackState.progress_ms)}</span>
                <span>{formatTime(playbackState.duration_ms)}</span>
              </div>
            </div>

            {/* Controls */}
            <div className="flex items-center justify-center space-x-8">
              <SkipBack 
                size={32} 
                className="text-zinc-400 hover:text-white transition-colors duration-200 cursor-pointer" 
              />
              <div className="w-16 h-16 rounded-full bg-white flex items-center justify-center hover:scale-105 transition-transform duration-200 cursor-pointer">
                {playbackState.is_playing ? (
                  <Pause size={32} className="text-black" />
                ) : (
                  <Play size={32} className="text-black ml-1" />
                )}
              </div>
              <SkipForward 
                size={32} 
                className="text-zinc-400 hover:text-white transition-colors duration-200 cursor-pointer" 
              />
            </div>

            {/* Volume Control */}
            <div className="flex items-center space-x-4">
              <Volume2 size={20} className="text-zinc-400" />
              <div className="flex-1 h-1 bg-zinc-800 rounded-full">
                <div className="h-full w-1/2 bg-white rounded-full hover:bg-green-500 transition-colors duration-200" />
              </div>
            </div>
          </div>
        </div>

        {/* Queue Section */}
        <div className="col-span-1">
          <QueueList />
        </div>
      </div>

      {/* Hand Status Indicator */}
      <div className="fixed bottom-6 left-1/2 -translate-x-1/2 bg-zinc-900/90 backdrop-blur-sm px-6 py-3 rounded-full border border-zinc-800 shadow-xl">
        <div className="flex items-center space-x-3">
          <div className={`w-3 h-3 rounded-full ${getStatusColor(handStatus)}`} />
          <span className="text-sm text-zinc-300">
            {handStatus === 'active' ? 'Hand Detected' :
             handStatus === 'warning' ? 'Hand at Edge' :
             'No Hand Detected'}
          </span>
        </div>
      </div>
    </div>
  );
};

export default MusicPlayer;
