import React, { useState, useEffect } from 'react';
import { Play, Pause, SkipBack, SkipForward, Volume2, Shuffle, Repeat } from 'lucide-react';

const MusicPlayer = () => {
  const [playbackState, setPlaybackState] = useState({
    track: '',
    artist: '',
    album_url: '',
    is_playing: false,
    progress_ms: 0,
    duration_ms: 0
  });
  const [handStatus, setHandStatus] = useState('inactive');
  const [volume, setVolume] = useState(50);
  const [isPinching, setIsPinching] = useState(false);

  useEffect(() => {
    const fetchPlayback = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/playback');
        const data = await response.json();
        if (!data.error) {
          setPlaybackState(data);
        }
      } catch (error) {
        console.error('Error fetching playback:', error);
      }
    };

    const fetchHandStatus = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/status');
        const data = await response.json();
        if (!data.error) {
          setHandStatus(data.hand_status);
          setVolume(data.current_volume);
          setIsPinching(data.is_pinching);
        }
      } catch (error) {
        console.error('Error fetching hand status:', error);
      }
    };

    fetchPlayback();
    fetchHandStatus();

    const playbackInterval = setInterval(fetchPlayback, 1000);
    const statusInterval = setInterval(fetchHandStatus, 100);

    return () => {
      clearInterval(playbackInterval);
      clearInterval(statusInterval);
    };
  }, []);

  const formatTime = (ms) => {
    const seconds = Math.floor(ms / 1000);
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return 'bg-green-500';
      case 'warning': return 'bg-yellow-500';
      default: return 'bg-red-500';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-zinc-900 to-black text-white">
      {/* Main content grid */}
      <div className="grid grid-cols-4 gap-8 p-8">
        {/* Album and controls section */}
        <div className="col-span-3">
          <div className="flex gap-8">
            {/* Album art - made smaller */}
            <div className="w-64 h-64 flex-shrink-0">
              <img 
                src={playbackState.album_url || '/api/placeholder/400/400'} 
                alt="Album Cover" 
                className="w-full h-full object-cover rounded-lg shadow-lg"
              />
            </div>

            {/* Track info and controls */}
            <div className="flex-1 flex flex-col justify-end pb-4">
              <h1 className="text-4xl font-bold mb-2">{playbackState.track || 'No track playing'}</h1>
              <p className="text-lg text-zinc-400 mb-8">{playbackState.artist || 'Unknown artist'}</p>

              {/* Playback controls */}
              <div className="space-y-6">
                <div className="flex items-center gap-6">
                  <Shuffle className="text-zinc-400 w-5 h-5" />
                  <SkipBack className="text-zinc-400 w-6 h-6" />
                  <button className="w-12 h-12 bg-white rounded-full flex items-center justify-center">
                    {playbackState.is_playing ? (
                      <Pause className="text-black w-6 h-6" />
                    ) : (
                      <Play className="text-black w-6 h-6 ml-1" />
                    )}
                  </button>
                  <SkipForward className="text-zinc-400 w-6 h-6" />
                  <Repeat className="text-zinc-400 w-5 h-5" />
                </div>

                {/* Progress bar */}
                <div className="flex items-center gap-2">
                  <span className="text-xs text-zinc-400 w-12">
                    {formatTime(playbackState.progress_ms)}
                  </span>
                  <div className="flex-1 h-1 bg-zinc-800 rounded-full">
                    <div 
                      className="h-full bg-white rounded-full"
                      style={{ width: `${(playbackState.progress_ms / playbackState.duration_ms) * 100}%` }}
                    />
                  </div>
                  <span className="text-xs text-zinc-400 w-12">
                    {formatTime(playbackState.duration_ms)}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Queue section */}
        <div className="col-span-1">
          <div className="bg-zinc-900/50 rounded-lg p-4 h-full">
            <h2 className="text-lg font-bold mb-4">Queue</h2>
            {/* Queue items would go here */}
          </div>
        </div>
      </div>

      {/* Bottom bar with volume and hand status */}
      <div className="fixed bottom-0 left-0 right-0 bg-zinc-900/95 border-t border-zinc-800 px-4 py-3">
        <div className="flex items-center justify-between max-w-7xl mx-auto">
          {/* Volume control */}
          <div className="flex items-center gap-4 w-1/4">
            <Volume2 className={`w-5 h-5 ${isPinching ? 'text-green-500' : 'text-zinc-400'}`} />
            <div className="flex-1 h-1 bg-zinc-800 rounded-full">
              <div 
                className={`h-full rounded-full transition-all duration-200 ${
                  isPinching ? 'bg-green-500' : 'bg-white'
                }`}
                style={{ width: `${volume}%` }}
              />
            </div>
            <span className="text-xs text-zinc-400 w-8">{volume}%</span>
          </div>

          {/* Hand status indicator */}
          <div className="flex items-center gap-2">
            <div className={`w-2 h-2 rounded-full ${getStatusColor(handStatus)}`} />
            <span className="text-sm text-zinc-400">
              {handStatus === 'active' ? 'Hand Detected' :
               handStatus === 'warning' ? 'Hand at Edge' :
               'No Hand Detected'}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MusicPlayer;
