            </div>

            {/* Progress Bar */}
            <div className="flex items-center space-x-2 w-full">
              <span className="text-xs text-zinc-400 w-10 text-right">
                {formatTime(playbackState.progress_ms)}
              </span>
              <div className="flex-1 h-1 bg-zinc-800 rounded-full">
                <div 
